import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  private apiUrl = 'http://localhost:5000/api/';  // Replace with your API endpoint

  constructor(private http: HttpClient) { }

  getData(): Observable<any> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiUHVzaGthciBTdXRhciIsImVtYWlsIjoicHVzaGthckB5b3BtYWlsLmNvbSIsImlkIjoiNjY1NjBmYWFkZWJiYWI5ZjRiNTNiZmJhIn0sImlhdCI6MTcxNzAwMTk1MCwiZXhwIjoxNzE3MDA3OTUwfQ.rU_vtoHyHe_sJg-mWjcUhbUktxdRMY31lBJYC1eluHQ'  // Replace with your token
    });

    return this.http.get( this.apiUrl + 'contacts', { headers });
  }

  createContact(data: any): Observable<any> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiUHVzaGthciBTdXRhciIsImVtYWlsIjoicHVzaGthckB5b3BtYWlsLmNvbSIsImlkIjoiNjY1NjBmYWFkZWJiYWI5ZjRiNTNiZmJhIn0sImlhdCI6MTcxNzAwMTk1MCwiZXhwIjoxNzE3MDA3OTUwfQ.rU_vtoHyHe_sJg-mWjcUhbUktxdRMY31lBJYC1eluHQ'  // Replace with your token
    });

    return this.http.post( this.apiUrl + 'contacts', data, { headers });
  }

  deleteContact(id: any): Observable<any> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7InVzZXJuYW1lIjoiUHVzaGthciBTdXRhciIsImVtYWlsIjoicHVzaGthckB5b3BtYWlsLmNvbSIsImlkIjoiNjY1NjBmYWFkZWJiYWI5ZjRiNTNiZmJhIn0sImlhdCI6MTcxNzAwMTk1MCwiZXhwIjoxNzE3MDA3OTUwfQ.rU_vtoHyHe_sJg-mWjcUhbUktxdRMY31lBJYC1eluHQ'  // Replace with your token
    });

    return this.http.delete(`${this.apiUrl + 'contacts'}/${id}`, { headers });
  }
}
