import { Component } from '@angular/core';
import { ApiService } from '../backend.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  constructor(private apiService: ApiService, private router: Router){}

  registerForm(form:any){
    console.log("This is the form",form)
    const data = {
      username: form.username,
      email: form.email,
      password: form.password
    }

    this.apiService.register(data).subscribe(
      response => {
        alert("You can now login")
        console.log("response",response);
      },
      error => {
        alert("Invalid Data")
        console.error('Error fetching data:', error);
      }
    );
  }

  navigate(){
    this.router.navigateByUrl("/login")
  }
}
<div class="body">
    <div class="login-container">
        <h2>Register</h2>
        <form #register = "ngForm" (ngSubmit)="registerForm(register.value)">
            <div class="form-group">
                <label for="username">Username:</label>
                <input type="text" class="form-control" id="username" name="username" required ngModel>
            </div>
            <div class="form-group">
                <label for="email">Email:</label>
                <input type="email" class="form-control" id="email" name="email" required ngModel>
            </div>
            <div class="form-group">
                <label for="password">Password:</label>
                <input type="password" class="form-control" id="password" name="password" required ngModel>
            </div>
            <button type="submit" class="btn btn-primary margin">Register</button>
            <button type="button" class="btn btn-primary marginTop" (click)="navigate()">Login</button>
        </form>
    </div>
</div>
